// The content is already a string when using simple: true
const fullText = $input.item.json.content;
const videoName = $('üìù EDIT THIS - Your Input').item.json.video_name;

// Split by section headers: # **Section N: Title**
const sectionRegex = /# \*\*Section (\d+): ([^*]+)\*\*/g;
const sections = [];
let match;
const matches = [];

// Find all section headers
while ((match = sectionRegex.exec(fullText)) !== null) {
  matches.push({
    index: match.index,
    fullMatch: match[0],
    number: parseInt(match[1]),
    title: match[2].trim()
  });
}

// Extract content for each section
for (let i = 0; i < matches.length; i++) {
  const currentMatch = matches[i];
  const startIndex = currentMatch.index;
  const endIndex = i < matches.length - 1 ? matches[i + 1].index : fullText.length;

  let sectionContent = fullText.substring(startIndex, endIndex).trim();

  // Remove the --- separator at the end if present
  sectionContent = sectionContent.replace(/\n---\s*$/, '').trim();

  sections.push({
    json: {
      video_name: videoName,
      chapter_number: currentMatch.number,
      script: sectionContent,
      status: 'pending'
    }
  });
}

if (sections.length === 0) {
  throw new Error('No sections found. Make sure the document uses "# **Section N: Title**" format.');
}

return sections;

// Google Sheets has a 50,000 character limit per cell
// We'll use 45,000 as a safe buffer
const MAX_CHARS = 45000;

const allItems = [];

// Function to find the best split point
function findSplitPoint(text, maxLength) {
  if (text.length <= maxLength) {
    return text.length;
  }

  // Try to split at a paragraph break (double newline)
  const paragraphBreak = text.lastIndexOf('\n\n', maxLength);
  if (paragraphBreak > maxLength * 0.8) { // Only use if it's at least 80% of max
    return paragraphBreak + 2; // Include the newlines
  }

  // Try to split at a sentence ending (. ! ? followed by space or newline)
  const sentenceEndings = ['. ', '.\n', '! ', '!\n', '? ', '?\n'];
  let bestSplit = -1;

  for (const ending of sentenceEndings) {
    const pos = text.lastIndexOf(ending, maxLength);
    if (pos > bestSplit) {
      bestSplit = pos + ending.length;
    }
  }

  if (bestSplit > maxLength * 0.8) { // Only use if it's at least 80% of max
    return bestSplit;
  }

  // Last resort: split at last space
  const lastSpace = text.lastIndexOf(' ', maxLength);
  if (lastSpace > 0) {
    return lastSpace + 1;
  }

  // Absolute last resort: hard cut (shouldn't happen with normal text)
  return maxLength;
}

// Process each section from the previous node
for (const item of $input.all()) {
  const script = item.json.script;
  const videoName = item.json.video_name;
  const chapterNumber = item.json.chapter_number;
  const status = item.json.status;

  // If section is short enough, keep it as-is
  if (script.length <= MAX_CHARS) {
    allItems.push({
      json: {
        video_name: videoName,
        chapter_number: chapterNumber,
        script: script,
        status: status
      }
    });
  } else {
    // Section is too long - split it into chunks at natural boundaries
    let remaining = script;
    let partNumber = 1;

    while (remaining.length > 0) {
      const splitPoint = findSplitPoint(remaining, MAX_CHARS);
      const chunk = remaining.substring(0, splitPoint).trim();

      // Format part number with leading zero (01, 02, etc.)
      const paddedPart = String(partNumber).padStart(2, '0');

      allItems.push({
        json: {
          video_name: videoName,
          chapter_number: `${chapterNumber}-${paddedPart}`,
          script: chunk,
          status: status
        }
      });

      remaining = remaining.substring(splitPoint).trim();
      partNumber++;
    }
  }
}

return allItems;

const seoResponse = $input.item.json.choices[0].message.content;
const videoName = $('üìù EDIT THIS - Your Input').item.json.video_name;

return [{
  json: {
    video_name: videoName,
    seo_package: seoResponse
  }
}];

